// tela de login

import React from 'react';
import { 
    View, 
    Text, 
    StyleSheet, 
    Image, 
    TextInput, 
    TouchableOpacity
} from 'react-native';

const image1 = require('../../assets/logo.png');

export default function SignIn(){
    return(
        <View style={styles.container}>
            <Image
                style={styles.logo}
                source={image1}
                />
            <View style={styles.inputContainer}>
                <TextInput
                    placeholder="Digite seu login"
                    style={styles.input} 
                    placeholderTextColor="#f0f0f0"
                    />

                <TextInput
                    placeholder="Digite sua senha"
                    style={styles.input} 
                    placeholderTextColor="#f0f0f0"
                    />
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Acessar</Text>
                </TouchableOpacity>
            </View>    
        </View>
    )
};

const styles = StyleSheet.create({
    container:{
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#1d1d2e'
    },
    logo:{
        marginBottom: 40
    },
    inputContainer:{
        width: '95%',
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 34,
        paddingHorizontal: 14,
    },
    input:{
        width:'95%',
        height: 40,
        backgroundColor: '#101026',
        marginBottom: 12,
        borderRadius: 4,
        paddingHorizontal: 8,
        color: '#fff',
    },
    button:{
        width:'95%',
        height: 40,
        backgroundColor: '#ff9d1a',
        borderRadius: 4,
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonText:{
        fontSize: 18,
        fontWeight: 'bold',
        color: '#101026'
    }
})
